struct_ADC rtadc;

char num[3];//数码管后三位

int rt_8bit=0;

int	rtnum=0;

void getrt(){

	rtadc=GetADC();

	rt_8bit=rtadc.Rt>>2;//10位转8位

	rtnum=arrThermLUT[rt_8bit-1];//寻找真实温度值

	num[0]=rtnum%1000/100;
	num[1]=rtnum%100/10;
	num[2]=rtnum%10;

	Seg7Print(0,0,0,0,0,num[0],num[1],num[2]);

}

void main() 
{ 	
