	DisplayerInit();
	SetDisplayerArea(0,7);
	LedPrint(0);
	Seg7Print(0,0,0,0,0,0,0,0);
	SetEventCallBack(enumEventSys100mS,getrt);//为了实时显示温度变化，100ms测量一次

